import { Component } from 'react';
import Header from '../Header/Header';
import Search from '../Search/Search';
import SearchResults from '../Results/SearchResults';
import GalleryResults from '../Results/GalleryResults';
import Gallery from '../Gallery/Gallery';
import Detail from '../Detail/Detail';
import playerRank from '../JsonData/PlayerRank.json';
import pictureMap from '../JsonData/PlayerRankPictureMap.json';
import allPlayers from '../JsonData/players.json';
import './App.css';
import axios from 'axios'
import {
  HashRouter as Router,
  Switch,
  Route,
  Link,
} from "react-router-dom";


class App extends Component {
  constructor(props) {
    super(props);
    this.state = { 
      players: [],
      searchPlayers: [],
      statistic : "pts",
      ordering : "",
      galleryPlayers: [],
    };
    this.handleSearch = this.handleSearch.bind(this);
    this.handleStat = this.handleStat.bind(this);
    this.handleSort = this.handleSort.bind(this);
    this.resetSearch = this.resetSearch.bind(this);
    this.handlePosition = this.handlePosition.bind(this);
    this.componentWillMount = this.componentWillMount.bind(this);
    this.getGalleryPlayers = this.getGalleryPlayers.bind(this);
    this.getDetailPlayers = this.getDetailPlayers.bind(this);
    this.getSearchPlayers = this.getSearchPlayers.bind(this);
  }

  componentWillMount() {
      var playerList = [];
      for (var j = 1; j <= 20; j++) {
        var fullName = playerRank[j].toString();
        axios.get('https://www.balldontlie.io/api/v1/players?search=' + fullName).then
        (res => {
          const player = res.data.data[0];
          let curPlayer = {};
          curPlayer.id = player.id;
          curPlayer.first_name = player.first_name;
          curPlayer.last_name = player.last_name;
          curPlayer.height_feet = player.height_feet;
          curPlayer.height_inches = player.height_inches;
          curPlayer.position = player.position;
          curPlayer.team = player.team;
          curPlayer.weight_pounds = player.weight_pounds;
          axios.get('https://www.balldontlie.io/api/v1/season_averages?season=2020&player_ids[]=' + player.id).then
          (res2 => {
            const stats = res2.data.data[0];
            curPlayer.min = stats.min;
            curPlayer.pts = stats.pts;
            curPlayer.fgm = stats.fgm;
            curPlayer.ftm = stats.ftm;
            curPlayer.fg3m = stats.fg3m;
            curPlayer.reb = stats.reb;
            curPlayer.ast = stats.ast;
            curPlayer.stl = stats.stl;
            curPlayer.blk = stats.blk;
            curPlayer.turnover = stats.turnover;
            curPlayer.fg_pct = stats.fg_pct;
            curPlayer.ft_pct = stats.ft_pct;
            curPlayer.fg3_pct = stats.fg3_pct;
          })
          .catch((error) => {
            console.log(error)
          })
          curPlayer.personId = pictureMap[curPlayer.id];
          playerList.push(curPlayer);
          return;
        })
        .catch((error) => {
          console.log(error)
        });
      }
      this.setState({players : playerList});
      this.setState({galleryPlayers : playerList});
      this.setState({searchPlayers : playerList});
  }

  handleSearch(event) {
    var name = event.target.value;
    name = name.replace(/\s/g,'').toLowerCase();
    var playersToSearch = this.state.players;
    if (this.state.players.length === 0 || this.state.players[0].pts === undefined) {
      playersToSearch = allPlayers;
    }
    const result = playersToSearch.filter(player => (
      player.first_name.toLowerCase().includes(name) || 
      player.last_name.toLowerCase().includes(name) ||
      player.first_name.toLowerCase().concat(player.last_name.toLowerCase()).includes(name)
    ));
    this.setState({ searchPlayers : result });
  }

  handleStat(event) {
    const index = event.target.selectedIndex;
    const statKey = event.target[index].value;
    this.setState({statistic : statKey});
  }

  handleSort(event) {
    const sortOrder = event.target.id;
    this.setState({ordering : sortOrder});
  }

  handlePosition(event) {
    const position = event.target.id;
    var result = this.state.players;
    var playersToFilter = this.state.players;
    if (this.state.players.length === 0 || this.state.players[0].pts === undefined) {
      result = allPlayers;
      playersToFilter = allPlayers;
    }
    switch (position) {
      case "PG":
        result = playersToFilter.filter(player => (
          player.position.includes("G") && !player.position.includes("F")
        ));
        break;
      case "SG":
        result = playersToFilter.filter(player => (
          player.position.includes("G")
        ));
        break;
      case "SF":
        result = playersToFilter.filter(player => (
          player.position.includes("F") && !player.position.includes("C")
        ));
        break;
      case "PF":
        result = playersToFilter.filter(player => (
          player.position.includes("F") && !player.position.includes("G")
        ));
        break;
      case "F":
      case "C":
      case "G":
        result = playersToFilter.filter(player => (
          player.position.includes(position)
        ));
        break;
      default:
        break;
    }
    this.setState({ galleryPlayers : result});
  }

  resetSearch() {
    if (document.getElementById("SearchInputID") != null) {
      document.getElementById("SearchInputID").value = "";
    }
    if (document.getElementById("StatSelectID") != null) {
      document.getElementById("StatSelectID").selectedIndex = 0;
    }
    if (document.getElementById("asc") != null) {
      document.getElementById('asc').checked = false;
    }
    if (document.getElementById("desc") != null) {
      document.getElementById('desc').checked = false;
    }
  }

  getSearchPlayers() {
    if (this.state.searchPlayers.length === 0 || this.state.players[0].pts === undefined) {
      return allPlayers;
    }
    return this.state.searchPlayers;
  }

  getGalleryPlayers() {
    if (this.state.galleryPlayers.length === 0 || this.state.players[0].pts === undefined) {
      return allPlayers;
    }
    return this.state.galleryPlayers;
  }

  getDetailPlayers() {
    if (this.state.players.length === 0 || this.state.players[0].pts === undefined) {
      return allPlayers;
    }
    return this.state.players;
  }

  render() {
    return (
        <Router>
        <div class="App">
          <Header />
          <div class="Options">
            <Link class = "SearchLink" to = {"/Search"}>
                <a class="Option1" href="/Search" onClick={this.resetSearch}>Search</a>
            </Link>
            <Link class = "GalleryLink" to = {"/Gallery"}>
                <a class="Option2" href="#Gallery">Gallery</a>
            </Link>
          </div>
        </div>

        <Switch>
            <Route exact path= {`/Search`}>
                <Search onSearchChange={this.handleSearch} onStatChange={this.handleStat} onSortChange={this.handleSort}/>
                <SearchResults players={this.getSearchPlayers()} statistic={this.state.statistic} order={this.state.ordering}/>
            </Route>   
            <Route exact path= {`/Gallery`} >
                <Gallery onPositionChange={this.handlePosition}/>
                <GalleryResults players={this.getGalleryPlayers()}/>
            </Route>
            { this.getDetailPlayers().map((player) => (
              <Route exact path= {`/Detail/` + player.id} >
                  <Detail player={player} statistic={this.state.statistic} players={this.getDetailPlayers()}/>
              </Route>   
            ))}
          </Switch>
      </Router>
    );
  }
}

export default App;
