import { Component } from 'react';

import Header from '../Header/Header';
import Search from '../Search/Search';
import SearchResults from '../Results/SearchResults';
import GalleryResults from '../Results/GalleryResults';
import Gallery from '../Gallery/Gallery';
import Detail from '../Detail/Detail';
import players from '../JsonData/players.json'
import './App.css';


class App extends Component {
  constructor(props) {
    super(props);
    this.state = { 
      searchPlayers: [],
      statistic : "pts",
      ordering : "",
      galleryPlayers: players
    };
    this.handleSearch = this.handleSearch.bind(this);
    this.handleStat = this.handleStat.bind(this);
    this.handleSort = this.handleSort.bind(this);
    this.displaySearch = this.displaySearch.bind(this);
    this.displayGallery = this.displayGallery.bind(this);
    this.handlePosition = this.handlePosition.bind(this);
  }

  handleSearch(event) {
    var name = event.target.value;
    name = name.replace(/\s/g,'').toLowerCase();
    const result = players.filter(player => (
      player.first_name.toLowerCase().includes(name) || 
      player.last_name.toLowerCase().includes(name) ||
      player.first_name.toLowerCase().concat(player.last_name.toLowerCase()).includes(name)
    ));
    this.setState({ searchPlayers : result });
  }

  handleStat(event) {
    const index = event.target.selectedIndex;
    const statKey = event.target[index].value;
    this.setState({statistic : statKey});
  }

  handleSort(event) {
    const sortOrder = event.target.id;
    this.setState({ordering : sortOrder});
  }

  handlePosition(event) {
    const position = event.target.id;
    if (position === "All") {
      this.setState({ galleryPlayers : players});
      return;
    }
    const result = players.filter(player => (
      player.position.includes(position)
    ));
    this.setState({ galleryPlayers : result});
  }

  displaySearch() {
    console.log("DisplaySearch");
    document.getElementById("GalleryID").style.display = 'none';
    document.getElementById("GalleryResultsID").style.display = 'none';
    this.setState({galleryPlayers : players})

    document.getElementById("SearchID").style.display = 'grid';
    document.getElementById("SearchResultsID").style.display = 'grid';
  }

  displayGallery() {
    console.log("DisplayGallery");
    document.getElementById("SearchID").style.display = 'none';
    document.getElementById("SearchResultsID").style.display = 'none';
    document.getElementById("SearchInputID").value = "";
    document.getElementById("StatSelectID").selectedIndex = 0;
    document.getElementById('asc').checked = false;
    document.getElementById('desc').checked = false;
    this.setState({ searchPlayers : [], statistic: "pts", ordering : "" });


    document.getElementById("GalleryID").style.display = 'grid';
    document.getElementById("GalleryResultsID").style.display = 'flex';
  }


  render() {
    return (
      <Router>
        <div class="App">
          <Header />
          <Link class = "SearchLink" to = {"/Search"}>
              <a class="Option1" href="#Search" onClick={this.displaySearch}>Search</a>
          </Link>
          <Link class = "GalleryLink" to = {"/Gallery"}>
              <a class="Option2" href="#Gallery" onClick={this.displayGallery}>Gallery</a>
          </Link>
          <Search onSearchChange={this.handleSearch} onStatChange={this.handleStat} onSortChange={this.handleSort}/>
        </div>

        <Switch>
            <Route path= {"/Search"} >
                <Search onSearchChange={this.handleSearch} onStatChange={this.handleStat} onSortChange={this.handleSort}/>
                <SearchResults players={this.state.searchPlayers} statistic={this.state.statistic} order={this.state.ordering}/>
            </Route>   
            <Route path= {"/Gallery"} >
                <Gallery onPositionChange={this.handlePosition}/>
                <GalleryResults players={this.state.galleryPlayers}/>
            </Route>  
          </Switch>
      </Router>
    );
  }
}

export default App;
