import { Component } from 'react';

import Header from '../Header/Header';
import Search from '../Search/Search';
import SearchResults from '../Results/SearchResults';
import GalleryResults from '../Results/GalleryResults';
import Gallery from '../Gallery/Gallery';
import Detail from '../Detail/Detail';
import playerRank from '../PlayerRank.json'
import allPlayers from '../JsonData/players.json'

//import {getPlayers} from '../ApiRequests.js';
import './App.css';
import '../ApiRequests.js';
import axios from 'axios'
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
} from "react-router-dom";


class App extends Component {
  constructor(props) {
    super(props);
    this.state = { 
      players: [],
      searchPlayers: [],
      statistic : "pts",
      ordering : "",
      galleryPlayers: [],
      loadedPlayers: 0
    };
    this.handleSearch = this.handleSearch.bind(this);
    this.handleStat = this.handleStat.bind(this);
    this.handleSort = this.handleSort.bind(this);
    this.resetSearch = this.resetSearch.bind(this);
    this.handlePosition = this.handlePosition.bind(this);
    this.componentDidMount = this.componentDidMount.bind(this);
  }

  componentDidMount() {
    if (this.state.loadedPlayers === 0) {
      var playerList = [];
      for (var j = 1; j <= 10; j++) {
        var fullName = playerRank[j].toString();
        axios.get('https://www.balldontlie.io/api/v1/players?search=' + fullName).then
        (res => {
          const player = res.data.data[0];
          let curPlayer = {};
          curPlayer.id = player.id;
          curPlayer.first_name = player.first_name;
          curPlayer.last_name = player.last_name;
          curPlayer.height_feet = player.height_feet;
          curPlayer.height_inches = player.height_inches;
          curPlayer.position = player.position;
          curPlayer.team = player.team;
          curPlayer.weight_pounds = player.weight_pounds;
          playerList.push(curPlayer);
        });
      }
      this.setState({players : playerList});
      this.setState({galleryPlayers : playerList});
      this.setState({loadedPlayers : 1});
    }
  }

  handleSearch(event) {
    var name = event.target.value;
    name = name.replace(/\s/g,'').toLowerCase();
    const result = this.state.players.filter(player => (
      player.first_name.toLowerCase().includes(name) || 
      player.last_name.toLowerCase().includes(name) ||
      player.first_name.toLowerCase().concat(player.last_name.toLowerCase()).includes(name)
    ));
    this.setState({ searchPlayers : result });
  }

  handleStat(event) {
    const index = event.target.selectedIndex;
    const statKey = event.target[index].value;
    this.setState({statistic : statKey});
  }

  handleSort(event) {
    const sortOrder = event.target.id;
    this.setState({ordering : sortOrder});
  }

  handlePosition(event) {
    const position = event.target.id;
    var result = this.state.players;
    switch (position) {
      case "PG":
        result = this.state.players.filter(player => (
          player.position.includes("G") && !player.position.includes("F")
        ));
        break;
      case "SG":
        result = this.state.players.filter(player => (
          player.position.includes("G")
        ));
        break;
      case "SF":
        result = this.state.players.filter(player => (
          player.position.includes("F") && !player.position.includes("C")
        ));
        break;
      case "PF":
        result = this.state.players.filter(player => (
          player.position.includes("F") && !player.position.includes("G")
        ));
        break;
      case "F":
      case "C":
      case "G":
        result = this.state.players.filter(player => (
          player.position.includes(position)
        ));
        break;
      default:
        break;
    }
    this.setState({ galleryPlayers : result});
  }

  resetSearch() {
    if (document.getElementById("SearchInputID") != null) {
      document.getElementById("SearchInputID").value = "";
    }
    if (document.getElementById("StatSelectID") != null) {
      document.getElementById("StatSelectID").selectedIndex = 0;
    }
    if (document.getElementById("asc") != null) {
      document.getElementById('asc').checked = false;
    }
    if (document.getElementById("desc") != null) {
      document.getElementById('desc').checked = false;
    }
  }


  render() {
    return (
        <Router>
        <div class="App">
          <Header />
          <div class="Options">
            <Link class = "SearchLink" to = {"/mp2/Search"}>
                <a class="Option1" href="/Search" onClick={this.resetSearch}>Search</a>
            </Link>
            <Link class = "GalleryLink" to = {"/mp2/Gallery"}>
                <a class="Option2" href="#Gallery">Gallery</a>
            </Link>
          </div>
        </div>

        <Switch>
            <Route exact path= {`/mp2/Search`}>
                <Search onSearchChange={this.handleSearch} onStatChange={this.handleStat} onSortChange={this.handleSort}/>
                <SearchResults players={this.state.searchPlayers} statistic={this.state.statistic} order={this.state.ordering}/>
            </Route>   
            <Route exact path= {`/mp2/Gallery`} >
                <Gallery onPositionChange={this.handlePosition}/>
                <GalleryResults players={this.state.galleryPlayers}/>
            </Route>
            { this.state.players.map((player) => (
              <Route exact path= {`/mp2/Detail/` + player.id} >
                  <Detail player={player} players={this.state.players}/>
              </Route>   
            ))}
          </Switch>
      </Router>
    );
  }
}

export default App;
