import { Component } from 'react';
import './Detail.css'
//import PropTypes from "prop-types";
import React from "react";
import {
    Link,
  } from "react-router-dom";

class Detail extends Component {
    /*
    constructor(props) {
        super(props);
    }  
    */

    render() {
        var prevPlayer = (this.props.players.findIndex(player => player.id===this.props.player.id) - 1) % this.props.player.length;
        var nextPlayer = (this.props.players.findIndex(player => player.id===this.props.player.id) + 1) % this.props.player.length;
        console.log(prevPlayer);
        console.log(nextPlayer);
        return (
            <div class="Detail" id = "DetailID">
                <Link to={"/Detail/" + this.props.players[prevPlayer].id}>
                    <a class="prev">&#10094;</a>
                </Link>
                <Link to={"/Detail/" + this.props.players[nextPlayer].id}>
                    <a class="next">&#10095;</a>
                </Link>
                <div class = "DetailLogo">
                    <img class="DetailTeamLogo" alt = "logo of team" src={"https://www.nba.com/.element/img/1.0/teamsites/logos/teamlogos_500x500/" + this.props.player.team.abbreviation.toLowerCase() + ".png"}/>
                </div>
                {this.props.player.id}
            </div>
        );
    }
}

export default Detail;
