import { Component } from 'react';
import './Detail.css'
//import PropTypes from "prop-types";
import positionMap from '../JsonData/PositionMap.json';
import teamColorMap from '../JsonData/TeamColorMap.json'
import React from "react";
import {
    Link,
  } from "react-router-dom";

function mod(n, m) {
    return ((n % m) + m) % m;
}

class Detail extends Component {
    constructor(props) {
        super(props);
        this.setBackgroundColor = this.setBackgroundColor.bind(this);
    }  

    setBackgroundColor() {
        if (document.getElementById("DetailID" + this.props.player.id) != null) {
            document.getElementById("DetailID" + this.props.player.id).style.backgroundColor = teamColorMap[this.props.player.team.abbreviation];
        }
    }

    render() {
        var curIndex = this.props.players.findIndex(player => player.id===this.props.player.id);
        var prevPlayer = mod(curIndex - 1, this.props.players.length);
        var nextPlayer = mod(curIndex + 1, this.props.players.length);

        return (
            <div class="Detail" id = {"DetailID" + this.props.player.id} onLoad= {this.setBackgroundColor}>
                <div class = "DetailLogo">
                    <img class="DetailTeamLogo" alt = "logo of team" src={"https://www.nba.com/.element/img/1.0/teamsites/logos/teamlogos_500x500/" + this.props.player.team.abbreviation.toLowerCase() + ".png"}/>
                </div>
                <div class = "DetailInfo">
                    <img class="DetailPlayerImage" alt = "headshot of player" src={"https://ak-static.cms.nba.com/wp-content/uploads/headshots/nba/latest/260x190/" + this.props.player.personId + ".png"}/>
                    <div class = "DetailPlayerInfo">
                        <h3 class = "TeamPosition">{this.props.player.team.full_name + " | " + positionMap[this.props.player.position]}</h3>
                        <h1 class = "DetailPlayerFirstName">{this.props.player.first_name}</h1>
                        <h1 class = "DetailPlayerLastName">{this.props.player.last_name}</h1>
                        <h3 class = "HeightWeight">{"Height: " + this.props.player.height_feet + "'" + this.props.player.height_inches + "\" | Weight: " + this.props.player.weight_pounds + "lb"}</h3>
                    </div>
                </div>
                <div class = "DetailPlayerStats">
                    <div class = "DetailStat">
                        <p class = "Stat">PPG</p>
                        <p class = "StatNumber">{this.props.player.pts}</p>
                    </div>
                    <div class = "DetailStat">
                        <p class = "Stat">REB</p>
                        <p class = "StatNumber">{this.props.player.reb}</p>
                    </div>
                    <div class = "DetailStat">
                        <p class = "Stat">AST</p>
                        <p class = "StatNumber">{this.props.player.ast}</p>
                    </div>
                    <div class = "DetailStat">
                        <p class = "Stat">STL</p>
                        <p class = "StatNumber">{this.props.player.stl}</p>
                    </div>
                    <div class = "DetailStat">
                        <p class = "Stat">BLK</p>
                        <p class = "StatNumber">{this.props.player.blk}</p>
                    </div>
                    <div class = "DetailStat">
                        <p class = "Stat">MIN</p>
                        <p class = "StatNumber">{this.props.player.min}</p>
                    </div>
                    <div class = "DetailStat">
                        <p class = "Stat">FG%</p>
                        <p class = "StatNumber">{this.props.player.fg_pct}</p>
                    </div>
                    <div class = "DetailStat">
                        <p class = "Stat">FT%</p>
                        <p class = "StatNumber">{this.props.player.ft_pct}</p>
                    </div>
                    <div class = "DetailStat">
                        <p class = "Stat">3P%</p>
                        <p class = "StatNumber">{this.props.player.fg3_pct}</p>
                    </div>
                    <div class = "DetailStat">
                        <p class = "Stat">TOV</p>
                        <p class = "StatNumber">{this.props.player.turnover}</p>
                    </div>
                    <Link class = "DetailArrow" to={"/mp2/Detail/" + this.props.players[prevPlayer].id}>
                        <a class="prev" href="/#">&#10094;</a>
                    </Link>
                    <Link class = "DetailArrow" to={"/mp2/Detail/" + this.props.players[nextPlayer].id}>
                        <a class="next" href="/#">&#10095;</a>
                    </Link>    
                </div>
            </div>
        );
    }
}


export default Detail;
