import { Component } from 'react';

import Header from '../Header/Header';
import Search from '../Search/Search';
import Results from '../Results/Results';
import Gallery from '../Gallery/Gallery';
import Detail from '../Detail/Detail';
import players from '../players.json'

import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { players };
    this.handleSearch = this.handleSearch.bind(this);
  }

  handleSearch(event) {
    const name = event.target.value;
    const result = players.filter(player => (
      player.first_name.toLowerCase().includes(name.toLowerCase()) || 
      player.last_name.toLowerCase().includes(name.toLowerCase())
    ));
    this.setState({ players : result });

    result.forEach(player => console.log(player.first_name));
    console.log(name);
    console.log("!!!!!!!!!!");
  }

  render() {
    return (
      <div class="App">
        <Header />
        <div class="Options">
          <a class="Option1" href="#Search">Search</a>
          <a class="Option2" href="#Gallery">Gallery</a>
        </div>
        <Search onChange={this.handleSearch} />
        <Results players={this.state.players} />
      </div>
    );
  }
}

//players.forEach(player => console.log(player.first_name));

export default App;
