import { Component } from 'react';

import Header from '../Header/Header';
import Search from '../Search/Search';
import Results from '../Results/Results';
import players from '../players.json'

import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { 
      players,
      statistic : "pts",
      ordering : "none"
    };
    this.handleSearch = this.handleSearch.bind(this);
    this.handleStat = this.handleStat.bind(this);
    this.handleSort = this.handleSort.bind(this);
    this.orderPlayers = this.orderPlayers.bind(this);
  }

  orderPlayers(order, unorderedPlayers) {
    if (order == "asc") {
      var sortedPlayers = unorderedPlayers;
      var statToSort = this.state.statistic;
      sortedPlayers.sort(function(a, b) {
        return a[statToSort] - b[statToSort];
      });
      this.setState({ players : sortedPlayers });
    } else if (order == "desc") {
      var sortedPlayers = unorderedPlayers;
      var statToSort = this.state.statistic;
      sortedPlayers.sort(function(a, b) {
        return b[statToSort] - a[statToSort];
      });
      this.setState({ players : sortedPlayers });
    } else {
      this.setState({ players : unorderedPlayers });
    }
  }

  handleSearch(event) {
    var name = event.target.value;
    name = name.replace(/\s/g,'').toLowerCase();
    const result = players.filter(player => (
      player.first_name.toLowerCase().includes(name) || 
      player.last_name.toLowerCase().includes(name) ||
      player.first_name.toLowerCase().concat(player.last_name.toLowerCase()).includes(name)
    ));
    this.orderPlayers(this.state.ordering, result)
    //this.setState({ players : result });
    //this.orderPlayers();
  }

  handleStat(event) {
    const index = event.target.selectedIndex;
    const statKey = event.target[index].value;
    this.setState({statistic : statKey});
  }

  handleSort(event) {
    const sortOrder = event.target.id;
    this.setState({ordering : sortOrder});
    //this.orderPlayers();

    if (sortOrder == "asc") {
      this.orderPlayers("asc", this.state.players)
      /*
      var sortedPlayers = this.state.players;
      var statToSort = this.state.statistic;
      console.log(sortedPlayers[0].first_name);
      console.log(statToSort);
      sortedPlayers.sort(function(a, b) {
        return a[statToSort] - b[statToSort];
      });
      this.setState({ players : sortedPlayers })
      */
    } else {
      this.orderPlayers("desc", this.state.players)
      /*
      var sortedPlayers = this.state.players;
      var statToSort = this.state.statistic;
      console.log(sortedPlayers[0].first_name);
      console.log(statToSort);
      sortedPlayers.sort(function(a, b) {
        return b[statToSort] - a[statToSort];
      });
      this.setState({ players : sortedPlayers })
      */
    }
  }

  render() {
    return (
      <div class="App">
        <Header />
        <div class="Options">
          <a class="Option1" href="#Search">Search</a>
          <a class="Option2" href="#Gallery">Gallery</a>
        </div>
        <Search onSearchChange={this.handleSearch} onStatChange={this.handleStat} onSortChange={this.handleSort}/>
        <Results players={this.state.players} statistic={this.state.statistic}/>
      </div>
    );
  }
}

//players.forEach(player => console.log(player.first_name));

export default App;
