import { Component } from 'react';
import './Results.css'
import PropTypes from "prop-types";

class Results extends Component {  
    constructor(props) {
        super(props);
        this.state = { 
          value : 0
        };
      }
    static propTypes = {
        players: PropTypes.arrayOf(
            PropTypes.shape({
                id: PropTypes.number.isRequired,
                first_name: PropTypes.string.isRequired,
                height_feet: PropTypes.number.isRequired,
                height_inches: PropTypes.number.isRequired,
                last_name: PropTypes.string.isRequired,
                position: PropTypes.string.isRequired,
                team: PropTypes.object.isRequired,
                weight_pounds: PropTypes.number.isRequired
            })
        ).isRequired,
        statistic: PropTypes.string.isRequired
    };

    static defaultProps = {
        players: [],
        statistic: ""
    }

    getStatistic(id, season = 2020) {
        const statKey = this.props.statistic;
        var value;
        console.log(statKey);

        var xhr = new XMLHttpRequest();
        xhr.onreadystatechange = xhrHandler;
        xhr.open('get', 'https://www.balldontlie.io/api/v1/season_averages?season=' +season+ '&player_ids[]='+id);
        xhr.send(null);

        function xhrHandler() {
            if (xhr.readyState === 4 && xhr.status === 200) {
                var data = JSON.parse(xhr.responseText);
                myFunction(data);
                this.setState({value : data.data[0][statKey]});
            }
        };
        function myFunction(data) {
            this.setState({value : data.data[0][statKey]});
        }
    }

    render() {
        this.getStatistic(this.props.players[0].id, 2020);
        return (
        <div class = "Results">

                    <div class = "Player-Item">
                        {this.props.players[0].first_name + " " + this.props.players[0].last_name + " " + this.state.value}
                    </div>

            </div>
        );
        /*
        return (
            <div class = "Results">

                {this.props.players.map((player) => (
                    <div class = "Player-Item">
                        {player.first_name + " " + player.last_name + " " + this.getStatistic(player, 2020)}
                    </div>
                ))}

            </div>
        );
        */
    }
}

export default Results;
