import { Component } from 'react';
import './Search.css'
import PropTypes from "prop-types";

class Search extends Component {
    constructor(props) {
        super(props);
        this.handleSearchChange = this.handleSearchChange.bind(this);
        this.handleStatChange = this.handleStatChange.bind(this);
        this.handleSortChange = this.handleSortChange.bind(this);
    }

    static propTypes = {
        onSearchChange: PropTypes.func,
        onStatChange: PropTypes.func,
        onSortChange: PropTypes.func
    };

    static defaultProps = {
        onSearchChange: () => { },
        onStatChange: () => { },
        onSortChange: () => { }
    }

    handleSearchChange(event) {
        const onSearchChange = this.props.onSearchChange;
        onSearchChange(event);
    }

    handleStatChange(event) {
        const onStatChange = this.props.onStatChange;
        onStatChange(event);
    }

    handleSortChange(event) {
        const onSortChange = this.props.onSortChange;
        onSortChange(event);
    }

    render() {
        return (
            <div className="Search" id = "SearchID">
                <div className = "InputWrapper">
                    <input className="SearchInput" id = "SearchInputID" placeholder="Player Search" onChange={this.handleSearchChange}/>
                </div>
                <div className = "SelectWrapper">
                    <select className="StatSelect" id = "StatSelectID" onChange={this.handleStatChange}>
                        <option value="" disabled selected>Select Stat</option>
                        <option value="pts">PTS</option>
                        <option value="min">MIN</option>
                        <option value="fgm">FGM</option>
                        <option value="fg3m">3PM</option>
                        <option value="ftm">FTM</option>
                        <option value="reb">REB</option>
                        <option value="ast">AST</option>
                        <option value="stl">STL</option>
                        <option value="blk">BLK</option>
                        <option value="turnover">TOV</option>
                        <option value="fg_pct">FG%</option>
                        <option value="fg3_pct">3P%</option>
                        <option value="ft_pct">FT%</option>
                    </select>
                </div>
                <div className = "SortWrapper">
                    <label className="SortAsc">
                        <input type="radio" name="optradio" id="asc" onChange={this.handleSortChange}/>Ascending
                    </label>
                    <label className="SortDesc">
                        <input type="radio" name="optradio" id="desc" onChange={this.handleSortChange}/>Descending
                    </label>
                </div>
            </div>
        );
    }
}

export default Search;
