import { Component } from 'react';
import './SearchResults.css'
import PropTypes from "prop-types";
import statMap from '../JsonData/StatMap.json'
import React from "react";
import {
  Link,
} from "react-router-dom";

const playerShape = {
    id: PropTypes.number,
    first_name: PropTypes.string,
    height_feet: PropTypes.number,
    height_inches: PropTypes.number,
    last_name: PropTypes.string,
    position: PropTypes.string,
    team: PropTypes.object,
    weight_pounds: PropTypes.number,
    min: PropTypes.string,
    fgm: PropTypes.number,
    fg3m: PropTypes.number,
    ftm: PropTypes.number,
    reb: PropTypes.number,
    ast: PropTypes.number,
    stl: PropTypes.number,
    blk: PropTypes.number,
    turnover: PropTypes.number,
    pts: PropTypes.number,
    fg_pct: PropTypes.number,
    fg3_pct: PropTypes.number,
    ft_pct: PropTypes.number
}

class SearchResults extends Component {  
    constructor(props) {
        super(props);
        this.players = this.props.players;
        this.orderPlayers = this.orderPlayers.bind(this);
    }

    orderPlayers() {
        var statToSort = this.props.statistic;
        if (this.props.order === "asc") {
          this.players.sort(function(a, b) {
            return a[statToSort] - b[statToSort];
          });
        } else if (this.props.order === "desc") {
          this.players.sort(function(a, b) {
            return b[statToSort] - a[statToSort];
          });
        }
    }

    render() {
        this.players = this.props.players;
        this.orderPlayers();

        return (
            <div class = "SearchResults" id = "SearchResultsID"> 

                {this.players.map((player) => (
                    <Link class = "PlayerLink" to={"/Detail/" + player.id}>
                        <div class = "SearchItem">
                            <div class = "PlayerImage">
                                <img class = "SearchImage" alt = "headshot of player" src = {"https://ak-static.cms.nba.com/wp-content/uploads/headshots/nba/latest/260x190/" + player.personId + ".png"}/>
                            </div>
                            <div class = "SearchInfo"> 
                                <h3 class = "PlayerName">{player.first_name + " " + player.last_name}</h3>
                                <h3 class = "PlayerStat">{statMap[this.props.statistic] + ": " + player[this.props.statistic]}</h3>
                            </div>
                            <div class = "TeamImage">
                                <img class="TeamLogo" alt = "logo of team" src={"https://www.nba.com/.element/img/1.0/teamsites/logos/teamlogos_500x500/" + player.team.abbreviation.toLowerCase() + ".png"}/>
                            </div>
                        </div>
                    </Link>
                ))}
            </div>
        ); 
    }

    static propTypes = {
        players: PropTypes.arrayOf(PropTypes.shape(playerShape)).isRequired,
        statistic: PropTypes.string.isRequired,
        order: PropTypes.string.isRequired
    };

    static defaultProps = {
        players: [],
        statistic: "",
        order: ""
    }   
}

export default SearchResults;
