import { Component } from 'react';
import './GalleryResults.css'
import Detail from '../Detail/Detail';
import PropTypes from "prop-types";
import statMap from '../JsonData/StatMap.json'
import teamMap from '../JsonData/TeamMap.json'

class GalleryResults extends Component {  
    constructor(props) {
        super(props);
    }

    render() {
        return (
            <div class = "GalleryResults" id = "GalleryResultsID">
                {this.players.map((player) => (
                            <div class = "PlayerItem">
                                <div class = "PlayerImage">
                                    <img class = "SearchImage" alt = "headshot of player" src = {"https://ak-static.cms.nba.com/wp-content/uploads/headshots/nba/latest/260x190/" + player.personId + ".png"}/>
                                </div>
                                <div class = "SearchInfo"> 
                                    <h3 class = "PlayerName">{player.first_name + " " + player.last_name}</h3>
                                    <h3 class = "PlayerStat">{statMap[this.props.statistic] + ": " + player[this.props.statistic]}</h3>
                                </div>
                                <div class = "TeamImage">
                                    <img class="TeamLogo" alt = "logo of team" src={"http://loodibee.com/wp-content/uploads/nba-" + teamMap[player.team.name] + "-480x480.png"}/>

                                </div>
                            </div>
                    ))}

            </div>
        );  
    }
}

export default GalleryResults;
