import { Component } from 'react';
import './Results.css'
import PropTypes from "prop-types";

class Results extends Component {  
    constructor(props) {
        super(props);
        this.players = this.props.players;
        this.orderPlayers = this.orderPlayers.bind(this);
    }

    orderPlayers() {
        var statToSort = this.props.statistic;
        if (this.props.order === "asc") {
          this.players.sort(function(a, b) {
            return a[statToSort] - b[statToSort];
          });
        } else if (this.props.order === "desc") {
          this.players.sort(function(a, b) {
            return b[statToSort] - a[statToSort];
          });
        }
    }

    render() {
        this.players = this.props.players;
        this.orderPlayers();

        return (
            <div class = "Results"> 

                {this.players.map((player) => (
                    <div class = "PlayerItem">
                        <div class = "PlayerImage">
                            <img class = "SearchImage" alt = "headshot of player" src = {"https://ak-static.cms.nba.com/wp-content/uploads/headshots/nba/latest/260x190/" + player.pic_id + ".png"}/>
                        </div>
                        {player.first_name + " " + player.last_name + " " + player[this.props.statistic]}

                    </div>
                ))}

            </div>
        );

    }

    static propTypes = {
        players: PropTypes.arrayOf(
            PropTypes.shape({
                id: PropTypes.number.isRequired,
                first_name: PropTypes.string.isRequired,
                height_feet: PropTypes.number.isRequired,
                height_inches: PropTypes.number.isRequired,
                last_name: PropTypes.string.isRequired,
                position: PropTypes.string.isRequired,
                team: PropTypes.object.isRequired,
                weight_pounds: PropTypes.number.isRequired,
                games_played: PropTypes.number.isRequired,
                player_id: PropTypes.number.isRequired,
                season: PropTypes.number.isRequired,
                min: PropTypes.string.isRequired,
                fgm: PropTypes.number.isRequired,
                fga: PropTypes.number.isRequired,
                fg3m: PropTypes.number.isRequired,
                fg3a: PropTypes.number.isRequired,
                ftm: PropTypes.number.isRequired,
                fta: PropTypes.number.isRequired,
                oreb: PropTypes.number.isRequired,
                dreb: PropTypes.number.isRequired,
                reb: PropTypes.number.isRequired,
                ast: PropTypes.number.isRequired,
                stl: PropTypes.number.isRequired,
                blk: PropTypes.number.isRequired,
                turnover: PropTypes.number.isRequired,
                pf: PropTypes.number.isRequired,
                pts: PropTypes.number.isRequired,
                fg_pct: PropTypes.number.isRequired,
                fg3_pct: PropTypes.number.isRequired,
                ft_pct: PropTypes.number.isRequired
            })
        ).isRequired,
        statistic: PropTypes.string.isRequired,
        order: PropTypes.string.isRequired
    };

    static defaultProps = {
        players: [],
        statistic: "",
        order: ""
    }
}

export default Results;
